SELECT CaseData.InsertTime,CaseData.Contact,ProdSet.[Name],PurchasePart.Width,PurchasePart.Length,SaleProd.[Count] FROM CaseData, ProdSet, PurchasePart, SaleProd,SaleData WHERE CaseData.CaseID = SaleData.CaseID 
AND ProdSet.ProdID = PurchasePart.ProdID AND SaleData.SaleID = SaleProd.SaleID AND PurchasePart.PurchasePID = SaleProd.PurchasePID AND SaleData.InsertTime > '2017/8/30' Group by CaseData.InsertTime,CaseData.Contact,ProdSet.[Name],PurchasePart.Width,PurchasePart.Length,SaleProd.[Count] 
